                        isNull: notes === null,
                        isUndefined: notes === undefined,
                        isEmpty: notes === '',
                        isWhitespace: typeof notes === 'string' && notes.trim() === ''
                      });

                      if (Array.isArray(notes) && notes.length > 0) {
                        return (
                          <ul>
                            {notes.map((item, i) => <li key={i}>{String(item)}</li>)}
