        columns={playerColumns}
        dataSource={players}
        rowKey={(r, i) => i}
        loading={loadingPlayers}
        pagination={{ pageSize: 8 }}
        bordered
        style={{ background: '#fff', borderRadius: 8 }}
      />
      <div style={{ textAlign: 'center', marginTop: 24 }}>
        <Button onClick={() => { setUser(null); setPage('home'); }}>退出登录</Button>
      </div>
    </div>
  );

  // 智能互动页面
  const renderAiInteraction = () => {
    const [activeTab, setActiveTab] = useState('assessment');
    const [formData, setFormData] = useState({
      assessment: {},
      training: {},
      match: {},
      nutrition: {}
    });
    const [results, setResults] = useState({});
    const [loading, setLoading] = useState(false);

    const handleFormChange = (tab, field, value) => {
      setFormData(prev => ({
        ...prev,
        [tab]: { ...prev[tab], [field]: value }
      }));
    };

    const handleSubmit = async (tab) => {
      setLoading(true);
      try {
        let endpoint = '';
        let data = {};

        switch(tab) {
          case 'assessment':
            endpoint = '/api/ai/player-assessment';
            data = {
              assessmentType: formData.assessment.type || 'comprehensive',
              basic: {
                name: formData.assessment.name || '',
                age: formData.assessment.age || '',
                position: formData.assessment.position || '',
                height: formData.assessment.height || '',
                weight: formData.assessment.weight || '',
                trainingYears: formData.assessment.trainingYears || ''
              },
              // 客观测试数据
              physical: {
                sprint30m: formData.assessment.sprint30m || '',
                sprint50m: formData.assessment.sprint50m || '',
                sprint100m: formData.assessment.sprint100m || '',
                run12min: formData.assessment.run12min || '',
                yoyoTest: formData.assessment.yoyoTest || '',
                shuttleRun: formData.assessment.shuttleRun || '',
                longJump: formData.assessment.longJump || '',
                pullUps: formData.assessment.pullUps || '',
                squat: formData.assessment.squat || ''
              },
              skills: {
                juggling: formData.assessment.juggling || '',
                ballControl: formData.assessment.ballControl || '',
                dribbling: formData.assessment.dribbling || '',
                shortPass: formData.assessment.shortPass || '',
                longPass: formData.assessment.longPass || '',
                passSpeed: formData.assessment.passSpeed || '',
                shootingAccuracy: formData.assessment.shootingAccuracy || '',
                shootingPower: formData.assessment.shootingPower || '',
                shootingDistance: formData.assessment.shootingDistance || ''
              },
              match: {
                games: formData.assessment.games || '',
                minutes: formData.assessment.minutes || '',
                goals: formData.assessment.goals || '',
                assists: formData.assessment.assists || '',
                passes: formData.assessment.passes || '',
                passRate: formData.assessment.passRate || ''
              },
              // 主观评估数据
              subjective: {
                videoUrl: formData.assessment.videoUrl || '',
                coachNotes: formData.assessment.coachNotes || '',
                trainingDuration: formData.assessment.trainingDuration || ''
              }
            };
            break;
          case 'training':
            endpoint = '/api/ai/training-plan';
            data = formData.training;
            break;
          case 'match':
            endpoint = '/api/ai/match-analysis';
            data = formData.match;
            break;
          case 'nutrition':
            endpoint = '/api/ai/nutrition-advice';
            data = formData.nutrition;
            break;
        }

        const response = await fetch(`http://localhost:5000${endpoint}`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(data)
        });

        const result = await response.json();
        if (result.success) {
          setResults(prev => ({ ...prev, [tab]: result }));
          message.success(`${tab === 'assessment' ? '球员评估' : tab === 'training' ? '训练计划' : tab === 'match' ? '比赛分析' : '营养建议'}生成成功！`);
        } else {
          message.error(result.error || '生成失败');
        }
      } catch (error) {
        console.error('AI功能错误:', error);
        message.error('网络错误，请重试');
      } finally {
        setLoading(false);
      }
    };

