      <div style={{ maxWidth: 1200, margin: '40px auto', padding: '0 20px' }}>
        <Title level={2} style={{ textAlign: 'center' }}>智能互动</Title>
        <Paragraph style={{ textAlign: 'center', fontSize: 16, marginBottom: 40 }}>
          AI驱动的足球技能分析、训练计划制定和比赛表现评估
        </Paragraph>

        <Tabs activeKey={activeTab} onChange={setActiveTab} type="card">
          <Tabs.TabPane tab="球员评估" key="assessment">
            <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <Title level={4}>球员综合评估</Title>
              <Paragraph>选择评估方式，输入球员数据，获得专业的技能分析报告</Paragraph>

              <Row gutter={[16, 16]}>
                <Col span={24}>
                  <Form.Item label="评估方式">
                    <Radio.Group 
                      value={formData.assessment.type || 'comprehensive'} 
                      onChange={(e) => handleFormChange('assessment', 'type', e.target.value)}
                    >
                      <Radio value="comprehensive">综合评估（推荐）</Radio>
                      <Radio value="objective">客观数据评估</Radio>
                      <Radio value="subjective">主观视频评估</Radio>
                    </Radio.Group>
                  </Form.Item>
                </Col>
              </Row>

              {/* 基础信息 */}
              <div style={{ marginBottom: 24 }}>
                <Title level={5}>基础信息</Title>
                <Row gutter={[16, 16]}>
                  <Col span={8}>
                    <Input 
                      placeholder="球员姓名" 
                      value={formData.assessment.name || ''}
                      onChange={(e) => handleFormChange('assessment', 'name', e.target.value)}
                    />
                  </Col>
                  <Col span={8}>
                    <Input 
                      placeholder="年龄" 
                      value={formData.assessment.age || ''}
                      onChange={(e) => handleFormChange('assessment', 'age', e.target.value)}
                    />
                  </Col>
                  <Col span={8}>
                    <Select 
                      placeholder="位置" 
                      value={formData.assessment.position || ''}
                      onChange={(value) => handleFormChange('assessment', 'position', value)}
                    >
                      <Select.Option value="前锋">前锋</Select.Option>
                      <Select.Option value="中场">中场</Select.Option>
                      <Select.Option value="后卫">后卫</Select.Option>
                      <Select.Option value="门将">门将</Select.Option>
                    </Select>
                  </Col>
                  <Col span={8}>
                    <Input 
                      placeholder="身高(cm)" 
                      value={formData.assessment.height || ''}
                      onChange={(e) => handleFormChange('assessment', 'height', e.target.value)}
                    />
                  </Col>
                  <Col span={8}>
                    <Input 
                      placeholder="体重(kg)" 
                      value={formData.assessment.weight || ''}
                      onChange={(e) => handleFormChange('assessment', 'weight', e.target.value)}
                    />
                  </Col>
                  <Col span={8}>
                    <Input 
                      placeholder="训练年限" 
                      value={formData.assessment.trainingYears || ''}
                      onChange={(e) => handleFormChange('assessment', 'trainingYears', e.target.value)}
                    />
                  </Col>
                </Row>
              </div>

              {/* 客观测试数据 */}
              {(formData.assessment.type === 'comprehensive' || formData.assessment.type === 'objective') && (
                <>
                  <div style={{ marginBottom: 24 }}>
                    <Title level={5}>体能测试数据</Title>
                    <Row gutter={[16, 16]}>
                      <Col span={8}>
                        <Input 
                          placeholder="30米冲刺(秒)" 
                          value={formData.assessment.sprint30m || ''}
                          onChange={(e) => handleFormChange('assessment', 'sprint30m', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="50米冲刺(秒)" 
                          value={formData.assessment.sprint50m || ''}
                          onChange={(e) => handleFormChange('assessment', 'sprint50m', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="100米冲刺(秒)" 
                          value={formData.assessment.sprint100m || ''}
                          onChange={(e) => handleFormChange('assessment', 'sprint100m', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="12分钟跑(米)" 
                          value={formData.assessment.run12min || ''}
                          onChange={(e) => handleFormChange('assessment', 'run12min', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="YoYo测试(级数)" 
                          value={formData.assessment.yoyoTest || ''}
                          onChange={(e) => handleFormChange('assessment', 'yoyoTest', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="折返跑(秒)" 
                          value={formData.assessment.shuttleRun || ''}
                          onChange={(e) => handleFormChange('assessment', 'shuttleRun', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="立定跳远(厘米)" 
                          value={formData.assessment.longJump || ''}
                          onChange={(e) => handleFormChange('assessment', 'longJump', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="引体向上(个)" 
                          value={formData.assessment.pullUps || ''}
                          onChange={(e) => handleFormChange('assessment', 'pullUps', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="深蹲(公斤)" 
                          value={formData.assessment.squat || ''}
                          onChange={(e) => handleFormChange('assessment', 'squat', e.target.value)}
                        />
                      </Col>
                    </Row>
                  </div>

                  <div style={{ marginBottom: 24 }}>
                    <Title level={5}>足球专项技能</Title>
                    <Row gutter={[16, 16]}>
                      <Col span={8}>
                        <Input 
                          placeholder="颠球次数(个)" 
                          value={formData.assessment.juggling || ''}
                          onChange={(e) => handleFormChange('assessment', 'juggling', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="控球时间(秒)" 
                          value={formData.assessment.ballControl || ''}
                          onChange={(e) => handleFormChange('assessment', 'ballControl', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="变向控球(秒)" 
                          value={formData.assessment.dribbling || ''}
                          onChange={(e) => handleFormChange('assessment', 'dribbling', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
                          placeholder="短传准确率(%)" 
                          value={formData.assessment.shortPass || ''}
                          onChange={(e) => handleFormChange('assessment', 'shortPass', e.target.value)}
                        />
                      </Col>
                      <Col span={8}>
                        <Input 
