          sprint100m: document.getElementById('sprint100m')?.value || '',
          run12min: document.getElementById('run12min')?.value || '',
          yoyoTest: document.getElementById('yoyoTest')?.value || '',
          shuttleRun: document.getElementById('shuttleRun')?.value || '',
          longJump: document.getElementById('longJump')?.value || '',
          pullUps: document.getElementById('pullUps')?.value || '',
          squat: document.getElementById('squat')?.value || ''
        },
        skills: {
          juggling: document.getElementById('juggling')?.value || '',
          ballControl: document.getElementById('ballControl')?.value || '',
          dribbling: document.getElementById('dribbling')?.value || '',
          shortPass: document.getElementById('shortPass')?.value || '',
          longPass: document.getElementById('longPass')?.value || '',
          passSpeed: document.getElementById('passSpeed')?.value || '',
          shootingAccuracy: document.getElementById('shootingAccuracy')?.value || '',
          shootingPower: document.getElementById('shootingPower')?.value || '',
          shootingDistance: document.getElementById('shootingDistance')?.value || ''
        },
        match: {
          games: document.getElementById('games')?.value || '',
          minutes: document.getElementById('minutes')?.value || '',
          goals: document.getElementById('goals')?.value || '',
          assists: document.getElementById('assists')?.value || '',
          passes: document.getElementById('passes')?.value || '',
          passRate: document.getElementById('passRate')?.value || ''
        }
      };

      // 调用API进行分析
      const response = await fetch('http://localhost:5000/api/ai/skill-test-analysis', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (result.success) {
        setSkillTestResults(result.analysis);
        message.success('技能测试分析完成！');
      } else {
        message.error(result.error || '分析失败，请重试');
      }
    } catch (error) {
      console.error('技能测试提交错误:', error);
      message.error('提交失败，请检查网络连接');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <Layout>
      <Header style={{ background: '#fff', boxShadow: '0 2px 8px #f0f1f2', padding: 0 }}>
        <Row align="middle" justify="space-between">
          <Col>
            <Row align="middle" style={{ height: 64 }}>
              <div style={{ display: 'flex', alignItems: 'center', height: 64, gap: 28 }}>
                <Image src={logoUrl} width={40} preview={false} style={{ marginLeft: 24, verticalAlign: 'middle' }} />
                <span style={{ fontWeight: 700, fontSize: 22, color: '#222', lineHeight: '40px', verticalAlign: 'middle' }}>华夏小将</span>
              </div>
              <Menu mode="horizontal" selectable={false} style={{ borderBottom: 'none', marginLeft: 32 }}>
                <Menu.Item key="player" icon={<UserOutlined />} onClick={() => setModal('player')}>小将注册</Menu.Item>
                <Menu.Item key="coach" icon={<TeamOutlined />} onClick={() => setModal('coach')}>教练入驻</Menu.Item>
                <Menu.Item key="ai-interaction" icon={<RobotOutlined />} onClick={() => setPage('ai-interaction')}>智能互动</Menu.Item>
                <Menu.Item key="parent-community" icon={<CommunityOutlined />} onClick={() => setPage('parent-community')}>家长社区</Menu.Item>
                <Menu.Item key="vision" icon={<TrophyOutlined />} onClick={() => setModal('vision')}>我们的愿景</Menu.Item>
              </Menu>
            </Row>
          </Col>
          <Col>
            {user ? (
              <Button onClick={() => { setUser(null); setPage('home'); }}>退出登录</Button>
            ) : (
              <Button type="primary" icon={<LoginOutlined />} style={{ marginRight: 32 }} onClick={() => setModal('login')}>
                登录
              </Button>
            )}
          </Col>
        </Row>
      </Header>
      <Content style={{ minHeight: 'calc(100vh - 64px)', background: '#fff' }}>
        {page === 'home' && renderHome()}
        {page === 'player-upload' && renderPlayerUpload()}
        {page === 'coach-view' && renderCoachView()}
        {page === 'ai-interaction' && renderAiInteraction()}
        {page === 'parent-community' && renderParentCommunity()}
        <Modal open={!!modal} onCancel={() => setModal(null)} footer={null} destroyOnClose title={
          modal === 'player' ? '小将注册' :
          modal === 'coach' ? '教练入驻' :
          modal === 'login' ? '登录' :
          modal === 'vision' ? '我们的愿景' : ''
        }>
          {renderModalContent()}
        </Modal>
      </Content>
    </Layout>
  );
}

export default App;
