import React, { useState } from 'react';
import { Layout, Menu, Button, Row, Col, Typography, Image, Modal, Form, Input, DatePicker, Select, message, Upload, Table, Tabs, Radio } from 'antd';
import { UserOutlined, TeamOutlined, TrophyOutlined, LoginOutlined, RobotOutlined, TeamOutlined as CommunityOutlined } from '@ant-design/icons';
import './index.css';
import axios from 'axios';

const { Header, Content } = Layout;
const { Title, Paragraph } = Typography;
const { Option } = Select;
const { TextArea } = Input;

// 占位小将logo
const logoUrl = 'https://cdn-icons-png.flaticon.com/512/861/861512.png';
// 占位小球员照片
const playerPhotos = [
  'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80',
  'https://images.unsplash.com/photo-1517649763962-0c623066013b?auto=format&fit=crop&w=400&q=80',
  'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80',
];

function App() {
  const [modal, setModal] = useState(null); // 'player' | 'coach' | 'login' | 'vision' | null
  const [form] = Form.useForm();
  const [coachForm] = Form.useForm();
  const [loginForm] = Form.useForm();
  const [page, setPage] = useState('home'); // 'home' | 'player-upload' | 'coach-view' | 'ai-interaction' | 'parent-community' | 'player-assessment'
  const [user, setUser] = useState(null); // {role, name}

  // 小将上传照片/视频功能
  const [photoFile, setPhotoFile] = useState(null);
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploaded, setUploaded] = useState({ photo: null, video: null });

  // 教练查看所有小将信息
  const [players, setPlayers] = useState([]);
  const [loadingPlayers, setLoadingPlayers] = useState(false);

  // AI互动页面状态
  const [aiActiveTab, setAiActiveTab] = useState('assessment');
  const [aiFormData, setAiFormData] = useState({
    assessment: {},
    training: {},
    match: {},
    nutrition: {}
  });
  const [aiResults, setAiResults] = useState({});
  const [aiLoading, setAiLoading] = useState(false);

  // 技能测试数据收集状态
  const [skillTestData, setSkillTestData] = useState({
    basic: { name: '', age: '', position: '', height: '', weight: '', trainingYears: '' },
    physical: { sprint30m: '', sprint50m: '', sprint100m: '', run12min: '', yoyoTest: '', shuttleRun: '', longJump: '', pullUps: '', squat: '' },
    skills: { juggling: '', ballControl: '', dribbling: '', shortPass: '', longPass: '', passSpeed: '', shootingAccuracy: '', shootingPower: '', shootingDistance: '' },
    match: { games: '', minutes: '', goals: '', assists: '', passes: '', passRate: '' },
    subjective: { videoUrl: '', coachNotes: '', trainingDuration: '' }
  });
  const [skillTestResult, setSkillTestResult] = useState(null);
  const [skillTestLoading, setSkillTestLoading] = useState(false);

  const [coaches, setCoaches] = useState([]);
  const [skillTestResults, setSkillTestResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleUpload = async () => {
    if (!photoFile && !videoFile) {
      message.warning('请至少上传一张照片或一个视频');
      return;
    }
    const formData = new FormData();
    if (photoFile) formData.append('photo', photoFile);
    if (videoFile) formData.append('video', videoFile);
    setUploading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setUploaded({ photo: res.data.photo, video: res.data.video });
      setPhotoFile(null);
      setVideoFile(null);
      message.success('上传成功！');
    } catch (err) {
      message.error(err.response?.data?.error || '上传失败');
    }
    setUploading(false);
  };

  const fetchPlayers = async () => {
    setLoadingPlayers(true);
    try {
      const res = await axios.get('http://localhost:5000/api/players');
      setPlayers(res.data);
    } catch (err) {
      message.error('获取小将信息失败');
    }
    setLoadingPlayers(false);
  };

  React.useEffect(() => {
    if (page === 'coach-view') {
      fetchPlayers();
    }
    // eslint-disable-next-line
  }, [page]);

  const playerColumns = [
    { title: '姓名', dataIndex: 'name', key: 'name' },
    { title: '性别', dataIndex: 'gender', key: 'gender' },
    { title: '出生日期', dataIndex: 'birthday', key: 'birthday' },
    { title: '身高', dataIndex: 'height', key: 'height' },
    { title: '司职', dataIndex: 'position', key: 'position' },
    { title: '城市', dataIndex: 'city', key: 'city' },
    { title: '联系电话', dataIndex: 'phone', key: 'phone' },
  ];

  // 小将注册表单内容
  const renderPlayerForm = () => (
    <Form
      form={form}
      layout="vertical"
      onFinish={async values => {
        const data = { ...values, birthday: values.birthday?.format('YYYY-MM-DD') };
        try {
          await axios.post('http://localhost:5000/api/player/register', data);
          setModal(null);
          form.resetFields();
          message.success('注册成功！');
        } catch (err) {
          message.error(err.response?.data?.error || '注册失败');
        }
      }}
    >
      <Form.Item label="姓名" name="name" rules={[{ required: true, message: '请输入姓名' }]}><Input /></Form.Item>
      <Form.Item label="性别" name="gender" rules={[{ required: true, message: '请选择性别' }]}><Select><Option value="男">男</Option><Option value="女">女</Option></Select></Form.Item>
      <Form.Item label="出生日期" name="birthday" rules={[{ required: true, message: '请选择出生日期' }]}><DatePicker style={{ width: '100%' }} /></Form.Item>
      <Form.Item label="身高(cm)" name="height" rules={[{ required: true, message: '请输入身高' }]}><Input type="number" /></Form.Item>
      <Form.Item label="司职" name="position" rules={[{ required: true, message: '请选择司职' }]}>
        <Select placeholder="请选择司职">
          <Option value="前锋">前锋</Option>
          <Option value="中场">中场</Option>
          <Option value="后卫">后卫</Option>
          <Option value="门将">门将</Option>
        </Select>
      </Form.Item>
      <Form.Item label="城市" name="city" rules={[{ required: true, message: '请输入城市' }]}><Select><Option value="北京">北京</Option><Option value="上海">上海</Option><Option value="广州">广州</Option><Option value="深圳">深圳</Option><Option value="其他">其他</Option></Select></Form.Item>
      <Form.Item label="联系电话" name="phone" rules={[{ required: true, message: '请输入联系电话' }]}><Input /></Form.Item>
      <Form.Item label="密码" name="password" rules={[{ required: true, message: '请输入密码' }]}><Input.Password /></Form.Item>
      <Form.Item><Button type="primary" htmlType="submit" block>提交注册</Button></Form.Item>
    </Form>
  );

  // 教练入驻表单内容
  const renderCoachForm = () => (
    <Form
      form={coachForm}
      layout="vertical"
      onFinish={async values => {
        try {
          await axios.post('http://localhost:5000/api/coach/register', values);
          setModal(null);
          coachForm.resetFields();
          message.success('教练入驻成功！');
        } catch (err) {
          message.error(err.response?.data?.error || '入驻失败');
        }
      }}
    >
      <Form.Item label="姓名" name="name" rules={[{ required: true, message: '请输入姓名' }]}><Input /></Form.Item>
      <Form.Item label="性别" name="gender" rules={[{ required: true, message: '请选择性别' }]}><Select><Option value="男">男</Option><Option value="女">女</Option></Select></Form.Item>
      <Form.Item label="教练级别" name="level" rules={[{ required: true, message: '请输入教练级别' }]}><Input placeholder="如：国家一级、C级、B级等" /></Form.Item>
      <Form.Item label="俱乐部/学校" name="club" rules={[{ required: true, message: '请输入俱乐部或学校名称' }]}><Input /></Form.Item>
      <Form.Item label="联系电话" name="phone" rules={[{ required: true, message: '请输入联系电话' }]}><Input /></Form.Item>
      <Form.Item label="密码" name="password" rules={[{ required: true, message: '请输入密码' }]}><Input.Password /></Form.Item>
      <Form.Item><Button type="primary" htmlType="submit" block>提交入驻</Button></Form.Item>
    </Form>
  );

  // 登录表单内容
  const renderLoginForm = () => (
    <Form
      form={loginForm}
      layout="vertical"
      onFinish={async values => {
        try {
          const res = await axios.post('http://localhost:5000/api/login', values);
          setModal(null);
          loginForm.resetFields();
          setUser({ role: values.role, name: values.name });
          if (values.role === 'player') setPage('player-upload');
          else if (values.role === 'coach') setPage('coach-view');
          message.success('登录成功！');
        } catch (err) {
          message.error(err.response?.data?.error || '登录失败');
        }
      }}
    >
      <Form.Item label="身份" name="role" rules={[{ required: true, message: '请选择身份' }]}><Select><Option value="player">小将</Option><Option value="coach">教练</Option></Select></Form.Item>
      <Form.Item label="姓名" name="name" rules={[{ required: true, message: '请输入姓名' }]}><Input /></Form.Item>
      <Form.Item label="密码" name="password" rules={[{ required: true, message: '请输入密码' }]}><Input.Password /></Form.Item>
      <Form.Item><Button type="primary" htmlType="submit" block>登录</Button></Form.Item>
    </Form>
  );

  // 登录后页面
  const renderPlayerUpload = () => (
    <div style={{ textAlign: 'center', marginTop: 80 }}>
      <Title level={2}>欢迎你，{user?.name}！</Title>
      <Paragraph>请上传你的小将照片和比赛视频：</Paragraph>
      <div style={{ maxWidth: 400, margin: '0 auto' }}>
        <Upload
          beforeUpload={file => {
            setPhotoFile(file);
            return false;
          }}
          accept="image/*"
          maxCount={1}
          showUploadList={photoFile ? [{ name: photoFile.name }] : false}
        >
          <Button icon={<UserOutlined />}>选择照片 (≤1M)</Button>
        </Upload>
        <br /><br />
        <Upload
          beforeUpload={file => {
            setVideoFile(file);
            return false;
          }}
          accept="video/*"
          maxCount={1}
          showUploadList={videoFile ? [{ name: videoFile.name }] : false}
        >
          <Button icon={<UserOutlined />}>选择视频 (≤100M)</Button>
        </Upload>
        <br /><br />
        <Button type="primary" onClick={handleUpload} loading={uploading}>上传</Button>
        {uploaded.photo && <div style={{ marginTop: 16 }}>已上传照片：{uploaded.photo}</div>}
        {uploaded.video && <div style={{ marginTop: 8 }}>已上传视频：{uploaded.video}</div>}
      </div>
      <Button style={{ marginTop: 32 }} onClick={() => { setUser(null); setPage('home'); }}>退出登录</Button>
    </div>
  );
  const renderCoachView = () => (
    <div style={{ maxWidth: 900, margin: '40px auto' }}>
      <Title level={2} style={{ textAlign: 'center' }}>欢迎你，教练{user?.name}！</Title>
      <Paragraph style={{ textAlign: 'center' }}>以下是所有已登记小将信息：</Paragraph>
      <Table
        columns={playerColumns}
        dataSource={players}
        rowKey={(r, i) => i}
        loading={loadingPlayers}
        pagination={{ pageSize: 8 }}
        bordered
        style={{ background: '#fff', borderRadius: 8 }}
      />
      <div style={{ textAlign: 'center', marginTop: 24 }}>
        <Button onClick={() => { setUser(null); setPage('home'); }}>退出登录</Button>
      </div>
    </div>
  );

  // AI表单处理函数
  const handleAiFormChange = (tab, field, value) => {
    setAiFormData(prev => ({
      ...prev,
      [tab]: { ...prev[tab], [field]: value }
    }));
  };

  const handleAiSubmit = async (tab) => {
    setAiLoading(true);
    try {
      let endpoint = '';
      let data = {};

      switch(tab) {
        case 'assessment':
          endpoint = '/api/ai/player-assessment';
          data = {
            assessmentType: aiFormData.assessment.type || 'comprehensive',
            basic: {
              name: aiFormData.assessment.name || '',
              age: aiFormData.assessment.age || '',
              position: aiFormData.assessment.position || '',
              height: aiFormData.assessment.height || '',
              weight: aiFormData.assessment.weight || '',
              trainingYears: aiFormData.assessment.trainingYears || ''
            },
            // 客观测试数据
            physical: {
              sprint30m: aiFormData.assessment.sprint30m || '',
              sprint50m: aiFormData.assessment.sprint50m || '',
              sprint100m: aiFormData.assessment.sprint100m || '',
              run12min: aiFormData.assessment.run12min || '',
              yoyoTest: aiFormData.assessment.yoyoTest || '',
              shuttleRun: aiFormData.assessment.shuttleRun || '',
              longJump: aiFormData.assessment.longJump || '',
              pullUps: aiFormData.assessment.pullUps || '',
              squat: aiFormData.assessment.squat || ''
            },
            skills: {
              juggling: aiFormData.assessment.juggling || '',
              ballControl: aiFormData.assessment.ballControl || '',
              dribbling: aiFormData.assessment.dribbling || '',
              shortPass: aiFormData.assessment.shortPass || '',
              longPass: aiFormData.assessment.longPass || '',
              passSpeed: aiFormData.assessment.passSpeed || '',
              shootingAccuracy: aiFormData.assessment.shootingAccuracy || '',
              shootingPower: aiFormData.assessment.shootingPower || '',
              shootingDistance: aiFormData.assessment.shootingDistance || ''
            },
            match: {
              games: aiFormData.assessment.games || '',
              minutes: aiFormData.assessment.minutes || '',
              goals: aiFormData.assessment.goals || '',
              assists: aiFormData.assessment.assists || '',
              passes: aiFormData.assessment.passes || '',
              passRate: aiFormData.assessment.passRate || ''
            },
            // 主观评估数据
            subjective: {
              videoUrl: aiFormData.assessment.videoUrl || '',
              coachNotes: aiFormData.assessment.coachNotes || '',
              trainingDuration: aiFormData.assessment.trainingDuration || ''
            }
          };
          break;
        case 'training':
          endpoint = '/api/ai/training-plan';
          data = aiFormData.training;
          break;
        case 'match':
          endpoint = '/api/ai/match-analysis';
          data = aiFormData.match;
          break;
        case 'nutrition':
          endpoint = '/api/ai/nutrition-advice';
          data = aiFormData.nutrition;
          break;
      }

      const response = await fetch(`http://localhost:5000${endpoint}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
      });

      const result = await response.json();
      if (result.success) {
        setAiResults(prev => ({ ...prev, [tab]: result }));
        message.success(`${tab === 'assessment' ? '球员评估' : tab === 'training' ? '训练计划' : tab === 'match' ? '比赛分析' : '营养建议'}生成成功！`);
      } else {
        message.error(result.error || '生成失败');
      }
    } catch (error) {
      console.error('AI功能错误:', error);
      message.error('网络错误，请重试');
    } finally {
      setAiLoading(false);
    }
  };

  // 智能互动页面
  const renderAiInteraction = () => {
    return (
      <div style={{ maxWidth: 1200, margin: '40px auto', padding: '0 20px' }}>
        <Title level={2} style={{ textAlign: 'center' }}>智能互动</Title>
        <Paragraph style={{ textAlign: 'center', fontSize: 16, marginBottom: 40 }}>
          AI驱动的训练计划制定、比赛表现评估和营养建议
        </Paragraph>

        <Tabs activeKey={aiActiveTab} onChange={setAiActiveTab} type="card">
          <Tabs.TabPane tab="训练计划" key="training">
            <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <Title level={4}>个性化训练计划</Title>
              <Paragraph>根据球员特点和目标制定科学的训练计划</Paragraph>

              <Row gutter={[16, 16]}>
                <Col span={8}>
                  <Input 
                    placeholder="球员年龄" 
                    value={aiFormData.training.age || ''}
                    onChange={(e) => handleAiFormChange('training', 'age', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Select 
                    placeholder="当前水平" 
                    value={aiFormData.training.level || ''}
                    onChange={(value) => handleAiFormChange('training', 'level', value)}
                  >
                    <Select.Option value="初学者">初学者</Select.Option>
                    <Select.Option value="进阶">进阶</Select.Option>
                    <Select.Option value="高级">高级</Select.Option>
                  </Select>
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="训练目标" 
                    value={aiFormData.training.goals || ''}
                    onChange={(e) => handleAiFormChange('training', 'goals', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="可用时间(小时/周)" 
                    value={aiFormData.training.availableTime || ''}
                    onChange={(e) => handleAiFormChange('training', 'availableTime', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="设备条件" 
                    value={aiFormData.training.equipment || ''}
                    onChange={(e) => handleAiFormChange('training', 'equipment', e.target.value)}
                  />
                </Col>
              </Row>

              <Button type="primary" onClick={() => handleAiSubmit('training')} loading={aiLoading} style={{ marginTop: 16 }}>
                {aiLoading ? '生成中...' : '生成训练计划'}
              </Button>

              {aiResults.training && (
                <div style={{ marginTop: 32, background: '#f0f9ff', border: '1px solid #91d5ff', borderRadius: 8, padding: 24 }}>
                  <Title level={4} style={{ color: '#1890ff' }}>训练计划</Title>
                  <div><b>训练周期：</b> {aiResults.training.plan?.duration}</div>
                  <div><b>训练强度：</b> {aiResults.training.plan?.intensity}</div>
                  <div style={{ marginTop: 12 }}>
                    <b>每周安排：</b>
                    <ul>{aiResults.training.plan?.weeklySchedule?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                  <div>
                    <b>重点训练项目：</b>
                    <ul>{aiResults.training.plan?.focusAreas?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                </div>
              )}
            </div>
          </Tabs.TabPane>

          <Tabs.TabPane tab="比赛分析" key="match">
            <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <Title level={4}>比赛表现分析</Title>
              <Paragraph>分析比赛视频和数据，提供战术建议</Paragraph>

              <Row gutter={[16, 16]}>
                <Col span={12}>
                  <Input 
                    placeholder="比赛视频URL" 
                    value={aiFormData.match.videoUrl || ''}
                    onChange={(e) => handleAiFormChange('match', 'videoUrl', e.target.value)}
                  />
                </Col>
                <Col span={12}>
                  <Input 
                    placeholder="球队名称" 
                    value={aiFormData.match.teamName || ''}
                    onChange={(e) => handleAiFormChange('match', 'teamName', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Select 
                    placeholder="比赛类型" 
                    value={aiFormData.match.matchType || ''}
                    onChange={(value) => handleAiFormChange('match', 'matchType', value)}
                  >
                    <Select.Option value="友谊赛">友谊赛</Select.Option>
                    <Select.Option value="联赛">联赛</Select.Option>
                    <Select.Option value="杯赛">杯赛</Select.Option>
                  </Select>
                </Col>
                <Col span={8}>
                  <Select 
                    placeholder="对手实力" 
                    value={aiFormData.match.opponentLevel || ''}
                    onChange={(value) => handleAiFormChange('match', 'opponentLevel', value)}
                  >
                    <Select.Option value="较弱">较弱</Select.Option>
                    <Select.Option value="相当">相当</Select.Option>
                    <Select.Option value="较强">较强</Select.Option>
                  </Select>
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="比赛结果" 
                    value={aiFormData.match.result || ''}
                    onChange={(e) => handleAiFormChange('match', 'result', e.target.value)}
                  />
                </Col>
              </Row>

              <Button type="primary" onClick={() => handleAiSubmit('match')} loading={aiLoading} style={{ marginTop: 16 }}>
                {aiLoading ? '分析中...' : '分析比赛'}
              </Button>

              {aiResults.match && (
                <div style={{ marginTop: 32, background: '#fff7e6', border: '1px solid #ffd591', borderRadius: 8, padding: 24 }}>
                  <Title level={4} style={{ color: '#fa8c16' }}>比赛分析结果</Title>
                  <div><b>整体表现：</b> {aiResults.match.analysis?.performance}</div>
                  <div><b>评分：</b> {aiResults.match.analysis?.rating}</div>
                  <div style={{ marginTop: 12 }}>
                    <b>关键时刻：</b>
                    <ul>{aiResults.match.analysis?.keyMoments?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                  <div>
                    <b>改进建议：</b>
                    <ul>{aiResults.match.analysis?.improvements?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                </div>
              )}
            </div>
          </Tabs.TabPane>

          <Tabs.TabPane tab="营养建议" key="nutrition">
            <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
              <Title level={4}>运动营养建议</Title>
              <Paragraph>根据训练强度和球员情况提供个性化营养方案</Paragraph>

              <Row gutter={[16, 16]}>
                <Col span={8}>
                  <Input 
                    placeholder="球员年龄" 
                    value={aiFormData.nutrition.age || ''}
                    onChange={(e) => handleAiFormChange('nutrition', 'age', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="体重(kg)" 
                    value={aiFormData.nutrition.weight || ''}
                    onChange={(e) => handleAiFormChange('nutrition', 'weight', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="身高(cm)" 
                    value={aiFormData.nutrition.height || ''}
                    onChange={(e) => handleAiFormChange('nutrition', 'height', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Select 
                    placeholder="训练强度" 
                    value={aiFormData.nutrition.intensity || ''}
                    onChange={(value) => handleAiFormChange('nutrition', 'intensity', value)}
                  >
                    <Select.Option value="低强度">低强度</Select.Option>
                    <Select.Option value="中等强度">中等强度</Select.Option>
                    <Select.Option value="高强度">高强度</Select.Option>
                  </Select>
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="训练时长(小时/周)" 
                    value={aiFormData.nutrition.duration || ''}
                    onChange={(e) => handleAiFormChange('nutrition', 'duration', e.target.value)}
                  />
                </Col>
                <Col span={8}>
                  <Input 
                    placeholder="训练频率(次/周)" 
                    value={aiFormData.nutrition.frequency || ''}
                    onChange={(e) => handleAiFormChange('nutrition', 'frequency', e.target.value)}
                  />
                </Col>
              </Row>

              <Button type="primary" onClick={() => handleAiSubmit('nutrition')} loading={aiLoading} style={{ marginTop: 16 }}>
                {aiLoading ? '生成中...' : '生成营养建议'}
              </Button>

              {aiResults.nutrition && (
                <div style={{ marginTop: 32, background: '#f6ffed', border: '1px solid #b7eb8f', borderRadius: 8, padding: 24 }}>
                  <Title level={4} style={{ color: '#52c41a' }}>营养建议</Title>
                  <div><b>每日热量需求：</b> {aiResults.nutrition.plan?.calories}</div>
                  <div><b>蛋白质需求：</b> {aiResults.nutrition.plan?.protein}</div>
                  <div style={{ marginTop: 12 }}>
                    <b>推荐食物：</b>
                    <ul>{aiResults.nutrition.plan?.foods?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                  <div>
                    <b>补充建议：</b>
                    <ul>{aiResults.nutrition.plan?.supplements?.map((item, i) => <li key={i}>{item}</li>)}</ul>
                  </div>
                </div>
              )}
            </div>
          </Tabs.TabPane>
        </Tabs>
      </div>
    );
  };

  // 家长社区页面
  const renderParentCommunity = () => (
    <div style={{ maxWidth: 1000, margin: '40px auto', padding: '0 20px' }}>
      <Title level={2} style={{ textAlign: 'center' }}>家长社区</Title>
      <Paragraph style={{ textAlign: 'center', fontSize: 16, marginBottom: 40 }}>
        连接足球家长，分享经验，共同成长
      </Paragraph>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
            <Title level={4}>热门讨论</Title>
            <div style={{ marginBottom: 16 }}>
              <div style={{ padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}>
                <div style={{ fontWeight: 500, marginBottom: 4 }}>如何帮助孩子提高足球技术？</div>
                <div style={{ color: '#666', fontSize: 14 }}>分享者：足球爸爸 • 2小时前 • 15回复</div>
              </div>
              <div style={{ padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}>
                <div style={{ fontWeight: 500, marginBottom: 4 }}>孩子踢球受伤了，应该怎么处理？</div>
                <div style={{ color: '#666', fontSize: 14 }}>分享者：小球员妈妈 • 5小时前 • 8回复</div>
              </div>
              <div style={{ padding: '12px 0', borderBottom: '1px solid #f0f0f0' }}>
                <div style={{ fontWeight: 500, marginBottom: 4 }}>推荐几个好的青少年足球俱乐部</div>
                <div style={{ color: '#666', fontSize: 14 }}>分享者：足球教练 • 1天前 • 23回复</div>
              </div>
              <div style={{ padding: '12px 0' }}>
                <div style={{ fontWeight: 500, marginBottom: 4 }}>孩子对足球失去兴趣了怎么办？</div>
                <div style={{ color: '#666', fontSize: 14 }}>分享者：困惑的家长 • 2天前 • 12回复</div>
              </div>
            </div>
            <Button type="primary">发布新话题</Button>
          </div>
        </Col>
        <Col xs={24} lg={8}>
          <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)', marginBottom: 24 }}>
            <Title level={4}>社区活动</Title>
            <div style={{ marginBottom: 16 }}>
              <div style={{ padding: '8px 0', borderBottom: '1px solid #f0f0f0' }}>
                <div style={{ fontWeight: 500, fontSize: 14 }}>周末亲子足球赛</div>
                <div style={{ color: '#666', fontSize: 12 }}>本周六 14:00</div>
              </div>
              <div style={{ padding: '8px 0', borderBottom: '1px solid #f0f0f0' }}>
                <div style={{ fontWeight: 500, fontSize: 14 }}>足球技术分享会</div>
                <div style={{ color: '#666', fontSize: 12 }}>下周三 19:00</div>
              </div>
              <div style={{ padding: '8px 0' }}>
                <div style={{ fontWeight: 500, fontSize: 14 }}>营养师讲座</div>
                <div style={{ color: '#666', fontSize: 12 }}>下周日 10:00</div>
              </div>
            </div>
            <Button>查看详情</Button>
          </div>

          <div style={{ background: '#fff', padding: 24, borderRadius: 8, boxShadow: '0 2px 8px rgba(0,0,0,0.1)' }}>
            <Title level={4}>专家问答</Title>
            <Paragraph style={{ fontSize: 14, color: '#666' }}>
              专业教练和营养师在线解答家长疑问
            </Paragraph>
            <Button type="primary">提问专家</Button>
          </div>
        </Col>
      </Row>

      <div style={{ textAlign: 'center', marginTop: 40 }}>
        <Button onClick={() => setPage('home')}>返回首页</Button>
      </div>
    </div>
  );

  // 主页内容
  const renderHome = () => (
    <div style={{ textAlign: 'center', marginTop: 80 }}>
      <Row justify="center" align="middle">
        <Col>
          <Image src={logoUrl} width={80} preview={false} style={{ marginRight: 16, verticalAlign: 'middle' }} />
        </Col>
        <Col>
          <Title level={1} style={{ display: 'inline-block', verticalAlign: 'middle', fontWeight: 700, fontSize: 56 }}>华夏小将</Title>
        </Col>
      </Row>
      <div style={{ marginTop: 24 }}>
        <Paragraph style={{ fontSize: 20, fontWeight: 500 }}>
          在短时间内完成小球员注册，让千里马不再埋没！
        </Paragraph>
        <Paragraph style={{ fontSize: 20, fontWeight: 500 }}>
          希望能够打开一扇门，让您能够兑现孩子的天赋。
        </Paragraph>
      </div>
      <div style={{ marginTop: 40 }}>
        <Row gutter={[16, 16]} justify="center">
          {playerPhotos.map((url, idx) => (
            <Col key={idx}>
              <Image src={url} width={180} height={120} style={{ objectFit: 'cover', borderRadius: 8 }} preview={false} />
            </Col>
          ))}
        </Row>
      </div>
    </div>
  );

  // 占位弹窗内容
  const renderModalContent = () => {
    if (modal === 'player') return renderPlayerForm();
    if (modal === 'coach') return renderCoachForm();
    if (modal === 'login') return renderLoginForm();
    if (modal === 'vision') return <div>我们的愿景内容（待补充）</div>;
    return null;
  };

  // 技能测试数据收集和提交
  const handleSkillTestSubmit = async () => {
    try {
      setIsAnalyzing(true);

      // 收集表单数据
      const formData = {
        basic: {
          name: document.getElementById('playerName')?.value || '',
          age: document.getElementById('playerAge')?.value || '',
          position: document.getElementById('playerPosition')?.value || '',
          height: document.getElementById('playerHeight')?.value || '',
          weight: document.getElementById('playerWeight')?.value || '',
          trainingYears: document.getElementById('trainingYears')?.value || ''
        },
        physical: {
          sprint30m: document.getElementById('sprint30m')?.value || '',
          sprint50m: document.getElementById('sprint50m')?.value || '',
          sprint100m: document.getElementById('sprint100m')?.value || '',
          run12min: document.getElementById('run12min')?.value || '',
          yoyoTest: document.getElementById('yoyoTest')?.value || '',
          shuttleRun: document.getElementById('shuttleRun')?.value || '',
          longJump: document.getElementById('longJump')?.value || '',
          pullUps: document.getElementById('pullUps')?.value || '',
          squat: document.getElementById('squat')?.value || ''
        },
        skills: {
          juggling: document.getElementById('juggling')?.value || '',
          ballControl: document.getElementById('ballControl')?.value || '',
          dribbling: document.getElementById('dribbling')?.value || '',
          shortPass: document.getElementById('shortPass')?.value || '',
          longPass: document.getElementById('longPass')?.value || '',
          passSpeed: document.getElementById('passSpeed')?.value || '',
          shootingAccuracy: document.getElementById('shootingAccuracy')?.value || '',
          shootingPower: document.getElementById('shootingPower')?.value || '',
          shootingDistance: document.getElementById('shootingDistance')?.value || ''
        },
        match: {
          games: document.getElementById('games')?.value || '',
          minutes: document.getElementById('minutes')?.value || '',
          goals: document.getElementById('goals')?.value || '',
          assists: document.getElementById('assists')?.value || '',
          passes: document.getElementById('passes')?.value || '',
          passRate: document.getElementById('passRate')?.value || ''
        }
      };

      // 调用API进行分析
      const response = await fetch('http://localhost:5000/api/ai/skill-test-analysis', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData)
      });

      const result = await response.json();

      if (result.success) {
        setSkillTestResults(result.analysis);
        message.success('技能测试分析完成！');
      } else {
        message.error(result.error || '分析失败，请重试');
      }
    } catch (error) {
      console.error('技能测试提交错误:', error);
      message.error('提交失败，请检查网络连接');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <Layout>
      <Header style={{ background: '#fff', boxShadow: '0 2px 8px #f0f1f2', padding: 0 }}>
        <Row align="middle" justify="space-between">
          <Col>
            <Row align="middle" style={{ height: 64 }}>
              <div style={{ display: 'flex', alignItems: 'center', height: 64, gap: 28 }}>
                <Image src={logoUrl} width={40} preview={false} style={{ marginLeft: 24, verticalAlign: 'middle' }} />
                <span style={{ fontWeight: 700, fontSize: 22, color: '#222', lineHeight: '40px', verticalAlign: 'middle' }}>华夏小将</span>
              </div>
              <Menu mode="horizontal" selectable={false} style={{ borderBottom: 'none', marginLeft: 32 }}>
                <Menu.Item key="player" icon={<UserOutlined />} onClick={() => setModal('player')}>小将注册</Menu.Item>
                <Menu.Item key="coach" icon={<TeamOutlined />} onClick={() => setModal('coach')}>教练入驻</Menu.Item>
                <Menu.Item key="ai-interaction" icon={<RobotOutlined />} onClick={() => setPage('ai-interaction')}>智能互动</Menu.Item>
                <Menu.Item key="parent-community" icon={<CommunityOutlined />} onClick={() => setPage('parent-community')}>家长社区</Menu.Item>
                <Menu.Item key="vision" icon={<TrophyOutlined />} onClick={() => setModal('vision')}>我们的愿景</Menu.Item>
              </Menu>
            </Row>
          </Col>
          <Col>
            {user ? (
              <Button onClick={() => { setUser(null); setPage('home'); }}>退出登录</Button>
            ) : (
              <Button type="primary" icon={<LoginOutlined />} style={{ marginRight: 32 }} onClick={() => setModal('login')}>
                登录
              </Button>
            )}
          </Col>
        </Row>
      </Header>
      <Content style={{ minHeight: 'calc(100vh - 64px)', background: '#fff' }}>
        {page === 'home' && renderHome()}
        {page === 'player-upload' && renderPlayerUpload()}
        {page === 'coach-view' && renderCoachView()}
        {page === 'ai-interaction' && renderAiInteraction()}
        {page === 'parent-community' && renderParentCommunity()}
        <Modal open={!!modal} onCancel={() => setModal(null)} footer={null} destroyOnClose title={
          modal === 'player' ? '小将注册' :
          modal === 'coach' ? '教练入驻' :
          modal === 'login' ? '登录' :
          modal === 'vision' ? '我们的愿景' : ''
        }>
          {renderModalContent()}
        </Modal>
      </Content>
    </Layout>
  );
}

export default App;
