              method: 'POST',
              body: formData
            });

            const uploadResult = await uploadResponse.json();
            if (uploadResult.success) {
              videoUrl = uploadResult.videoUrl;
            } else {
              message.error('视频上传失败：' + uploadResult.error);
              setAiLoading(false);
