import React, { useState } from 'react';
import { Layout, Menu, Form, Input, Button, DatePicker, message, Upload, Select } from 'antd';
import { UploadOutlined, VideoCameraOutlined } from '@ant-design/icons';
import axios from 'axios';
//import 'antd/dist/antd.css';

const { Header, Content } = Layout;
const { Option } = Select;

function App() {
  const [step, setStep] = useState(1); // 1:注册表单 2:上传文件
  const [playerId, setPlayerId] = useState(null);
  const [form] = Form.useForm();
  const [uploading, setUploading] = useState(false);
  const [photoFile, setPhotoFile] = useState(null);
  const [videoFile, setVideoFile] = useState(null);

  // 注册表单提交
  const onFinish = async (values) => {
    try {
      await axios.post('http://localhost:5000/api/register', values);
      setStep(2);
      setPlayerId(values.name); // 用姓名做简单标识
      message.success('注册成功，请上传照片和视频');
    } catch (err) {
      message.error('注册失败');
    }
  };

  // 文件上传
  const handleUpload = async () => {
    if (!photoFile && !videoFile) {
      message.warning('请至少上传一张照片或一个视频');
      return;
    }
    const formData = new FormData();
    if (photoFile) formData.append('photo', photoFile);
    if (videoFile) formData.append('video', videoFile);
    setUploading(true);
    try {
      await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      message.success('上传成功！');
      setPhotoFile(null);
      setVideoFile(null);
    } catch (err) {
      message.error(err.response?.data?.error || '上传失败');
    }
    setUploading(false);
  };

  return (
    <Layout>
      <Header>
        <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['register']}>
          <Menu.Item key="register">球员注册</Menu.Item>
          <Menu.Item key="coach">教练入驻</Menu.Item>
        </Menu>
      </Header>
      <Content style={{ padding: '40px', maxWidth: 600, margin: '0 auto' }}>
        {step === 1 && (
          <Form
            form={form}
            layout="vertical"
            onFinish={onFinish}
            initialValues={{ city: '北京' }}
          >
            <Form.Item label="姓名" name="name" rules={[{ required: true, message: '请输入姓名' }]}> <Input /> </Form.Item>
            <Form.Item label="出生日期" name="birthday" rules={[{ required: true, message: '请选择出生日期' }]}> <DatePicker style={{ width: '100%' }} /> </Form.Item>
            <Form.Item label="身高(cm)" name="height" rules={[{ required: true, message: '请输入身高' }]}> <Input type="number" /> </Form.Item>
            <Form.Item label="司职" name="position" rules={[{ required: true, message: '请输入司职' }]}> <Input /> </Form.Item>
            <Form.Item label="城市" name="city" rules={[{ required: true, message: '请输入城市' }]}> <Select> <Option value="北京">北京</Option> <Option value="上海">上海</Option> <Option value="广州">广州</Option> <Option value="深圳">深圳</Option> <Option value="其他">其他</Option> </Select> </Form.Item>
            <Form.Item label="监护人姓名" name="guardian" rules={[{ required: true, message: '请输入监护人姓名' }]}> <Input /> </Form.Item>
            <Form.Item label="联系电话" name="phone" rules={[{ required: true, message: '请输入联系电话' }]}> <Input /> </Form.Item>
            <Form.Item>
              <Button type="primary" htmlType="submit">提交注册</Button>
            </Form.Item>
          </Form>
        )}
        {step === 2 && (
          <div>
            <h2>上传照片和视频</h2>
            <Upload
              beforeUpload={file => {
                setPhotoFile(file);
                return false;
              }}
              accept="image/*"
              maxCount={1}
              showUploadList={photoFile ? [{ name: photoFile.name }] : false}
            >
              <Button icon={<UploadOutlined />}>选择照片 (≤1M)</Button>
            </Upload>
            <br /><br />
            <Upload
              beforeUpload={file => {
                setVideoFile(file);
                return false;
              }}
              accept="video/*"
              maxCount={1}
              showUploadList={videoFile ? [{ name: videoFile.name }] : false}
            >
              <Button icon={<VideoCameraOutlined />}>选择视频 (≤100M)</Button>
            </Upload>
            <br /><br />
            <Button type="primary" onClick={handleUpload} loading={uploading}>上传</Button>
          </div>
        )}
      </Content>
    </Layout>
  );
}

export default App;
