sourcesContent:
import React, { useState } from 'react';
import { Layout, Menu, Button, Row, Col, Typography, Image, Modal, Form, Input, DatePicker, Select, message, Upload, Table } from 'antd';
import { UserOutlined, TeamOutlined, TrophyOutlined, LoginOutlined, RobotOutlined, TeamOutlined as CommunityOutlined } from '@ant-design/icons';
import './index.css';
import axios from 'axios';

const { Header, Content } = Layout;
const { Title, Paragraph } = Typography;
const { Option } = Select;

// 占位小将logo
const logoUrl = 'https://cdn-icons-png.flaticon.com/512/861/861512.png';
// 占位小球员照片
const playerPhotos = [
  'https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=crop&w=400&q=80',
  'https://images.unsplash.com/photo-1517649763962-0c623066013b?auto=format&fit=crop&w=400&q=80',
  'https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=crop&w=400&q=80',
];

function App() {
  const [modal, setModal] = useState(null); // 'player' | 'coach' | 'login' | 'vision' | null
  const [form] = Form.useForm();
  const [coachForm] = Form.useForm();
  const [loginForm] = Form.useForm();
  const [page, setPage] = useState('home'); // 'home' | 'player-upload' | 'coach-view' | 'ai-interaction' | 'parent-community'
  const [user, setUser] = useState(null); // {role, name}

  // 小将上传照片/视频功能
  const [photoFile, setPhotoFile] = useState(null);
  const [videoFile, setVideoFile] = useState(null);
  const [uploading, setUploading] = useState(false);
  const [uploaded, setUploaded] = useState({ photo: null, video: null });

  // 教练查看所有小将信息
  const [players, setPlayers] = useState([]);
  const [loadingPlayers, setLoadingPlayers] = useState(false);

  // AI互动页面状态
  const [activeTab, setActiveTab] = useState('assessment');
  const [aiLoading, setAiLoading] = useState(false);
  const [aiResults, setAiResults] = useState({});
  const [aiForms, setAiForms] = useState({
    assessment: { age: '', position: '', trainingDuration: '' },
    training: { age: '', level: '', goals: '', availableTime: '', equipment: '' },
    match: { teamName: '', matchType: '', opponentLevel: '' },
    nutrition: { age: '', weight: '', height: '', intensity: '', duration: '', frequency: '' }
  });

  // 技能测试数据收集状态
  const [testData, setTestData] = useState({
    // 基础信息
    basic: {
      name: '',
      age: '',
      position: '',
      height: '',
      weight: '',
      trainingYears: ''
    },

    // 体能测试
    physical: {
      sprint30m: '',
      sprint50m: '',
      sprint100m: '',
      run12min: '',
      yoyoTest: '',
      shuttleRun: '',
      longJump: '',
      pullUps: '',
      squat: '',
      sitReach: '',
      shoulderFlex: ''
    },

    // 足球专项技能
    skills: {
      juggling: '',
      ballControl: '',
      dribbling: '',
      shortPass: '',
      longPass: '',
      passSpeed: '',
      shootingAccuracy: '',
      shootingPower: '',
      shootingDistance: '',
      tackleRate: '',
      interception: '',
      defendingPosition: ''
    },

    // 比赛表现
    match: {
      games: '',
      minutes: '',
      goals: '',
      assists: '',
      passes: '',
      passRate: '',
      shots: '',
      shotsOnTarget: '',
      tackles: '',
      fouls: '',
      // 位置特定数据
      striker: { conversionRate: '', offsides: '', dribbles: '' },
      midfielder: { passAccuracy: '', keyPasses: '', possession: '' },
      defender: { clearances: '', interceptions: '', fouls: '' },
      goalkeeper: { saves: '', saveRate: '', cleanSheets: '' }
    },

    // 身体测量
    body: {
      height: '',
      weight: '',
      bodyFat: '',
      muscleMass: '',
      bmr: ''
    }
  });

  const [coaches, setCoaches] = useState([]);
  const [skillTestResults, setSkillTestResults] = useState(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const handleUpload = async () => {
    if (!photoFile && !videoFile) {
      message.warning('请至少上传一张照片或一个视频');
      return;
    }
    const formData = new FormData();
    if (photoFile) formData.append('photo', photoFile);
    if (videoFile) formData.append('video', videoFile);
    setUploading(true);
    try {
      const res = await axios.post('http://localhost:5000/api/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
